M = len(board)
N = len(board[0])


def walk(x, y, path, word):
  if len(word) == 0:
    return True
  if x >= 0 and x < M and y >= 0 and y < N and (x, y) not in path and board[x][y] == word[0]:
    path2 = path.copy()
    path2.add((x, y))
    if walk(x, y+1, path2, word[1:]) or walk(x, y-1, path2, word[1:]) or walk(x+1, y, path2, word[1:]) or walk(x-1, y, path2, word[1:]):
      return True
  return False


def exists(word):
  for i in range(M):
    for j in range(N):
      if walk(i, j, set(), word):
        return True
  return False


